import axios from "axios";
import {getRecipes,getCtegory}from './actions'
import * as actiontype from './actions'
const initialState={
    userId:null,
    recipes:[],
    selected:null
    
}

const reducer =(state=initialState,action)=>{
    switch(action.type)
    {
        case actiontype.LOD_OUT: return{...state ,userId:null,selected:null,recipes:[]}
        break;
        case actiontype.LON_IN:return{...state,userId:action.UserId}
        break;
        // case actiontype.GET_RECIPES: 
        // const recipes = [...state.recipes];
        // recipes.push(action.recipe);
        // return {...state, recipes };
        // break;
        case actiontype.ADD_RECIPE :return {...state,recipes:[...state.recipes,action.add]}
        case actiontype.DELETE_RECIPE :
        const findIndex = recipes.findIndex(x => x.Id == action.id);
        axios.post(`http://localhost:8080/api/recipe/delete/${action.Id}`)
        .then((x)=>{console.log(x.data);alert("נמחק");})
        case actiontype.SET_CATEGORY:return{...state,selected:action.selected}
        // case actiontype.SET_CATEDORY:return{...state,...state.Selected.category=action.category,...state.Selectedduration=action.duration,...state.Selected.difficulty=action.difficulty}
        break;
        default: return{...state}           
    }
   
    
}
export default reducer;
