import axios from "axios";
import { useState, useEffect } from "react";
export default () => {
  
  const [recipes, SetRecipes] = useState([]);
  const [categories, SetCategories] = useState([]);
  const [SelectedCategory, SetSelectedCategory] = useState([]);
  const func=(id) =>{
    console.log(id)
    if (categories.find(id) != undefined);
  }
  useEffect(() => {
    axios.get("http://localhost:8080/api/recipe")
      .then((data) => {
        SetRecipes(data.data)
      });
    axios.get("http://localhost:8080/api/category")
      .then((data) => {
        SetCategories(data.data)
      });
  }, [SelectedCategory]); 
  console.log(SelectedCategory);
  return <>
    <div id="categories">{categories.map((x) =>
      <div><input type="checkbox" name={x.Id} onClick={() => { func(x.Id)}} autofocus  />{x.Name}<br /></div>
    )}
   
    </div>
    <hi>מתכונים-</hi>
    {recipes.map((x) => <div class="recipy">{x.Name}>Img variant="top" src={recipe.Img}style={{width:'15rem', height:'12rem'}} /
    </div>)}</>
}
