import axios from "axios";
import { useState, useEffect } from "react";
import './Recipes.css'
import { useSelector } from "react-redux";
let my:null;
export default () => {
  const user = useSelector(state => (state.userId));
  const [recipes, SetRecipes] = useState([]);
  const [categories, SetCategories] = useState([]);
  const [SelectedCategory, SetSelectedCategory] = useState([]);
  const func = (id) => {
    console.log(id)
    if (categories.find(id) != undefined);
  }
  useEffect(() => {
    axios.get("http://localhost:8080/api/recipe")
      .then((data) => {
        SetRecipes(data.data)
      });
    axios.get("http://localhost:8080/api/category")
      .then((data) => {
        SetCategories(data.data)
      });
  }, [SelectedCategory]);
  console.log(SelectedCategory);
  console.log(user);
  return <>
  {/* <input type="checkbox" name={x.Id} onClick={() => { func(x.Id) }} /> */}
    <select id="categories"> קטגוריה{categories.map((x) =>
      <option name={x.Id}>{x.Name}</option>
    )}
    </select>
    <select id="diffic"> :דרגת קושי
      <option name="1">1</option>
      <option name="2">2</option>
      <option name="3">3</option>     
    </select>
    <p>duretion:</p>
    <input type="range" min="1" max="100" value="50" class="slider" id="myRange"></input>
    
    <hi id="recipes"> -מתכונים- </hi>
    {recipes.map((x) => <div class="recipy"><h2>{x.Name}</h2><br />
      <img src={x.Img} style={{ width: '15rem', height: '12rem'}} /><br />
      <button class="but">מוצרים</button>
      <button class="but">מתכון</button>
      {user == x.Id &&<button class="but">עריכה</button> }
       {user == x.Id &&<button class="but">מחיקה</button>}
    </div>)}</>
}
