import { useForm, useFieldArray } from "react-hook-form"
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup"
import { useLocation } from "react-router-dom"
import axios from "axios"
import { useDispatch, useSelector } from "react-redux"
import * as actiontype from '../Store/actions'
import { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const schema = yup
    .object({

        Name: yup.string().required(),
        UserId: yup.number().required(),
        CategoryId: yup.number().required(),
        Img: yup.string().required(),
        Duration: yup.number().required(),
        Difficulty: yup.number().required(),
        Description: yup.string(),
        Ingrident: yup.array().of(yup.object().shape({
            Name: yup.string().required().nullable("null").required("not null"),
            Count: yup.number().required().nullable("null").required("not null"),
            Type: yup.string().nullable("null").required("not null"),
        })
        ),
        Instructions: yup.array().of(yup.object().shape({
            Instruction: yup.string().required(),
        }),
        )
    })
// const Input=({register,errors,label,name,placeholder})=>{
//     return <><label>{label}</label>
//     <br/>
//     <input placeholder={placeholder} {...register(name)}/>
//     <br/>
//     <p>{errors?.message}</p>
//     </>
// }
const Input = ({ register, errors, label, name }) => {
    return <>
        <label>{label}</label><br />
        <input  {...register({ name })} /><br />
        <p>{errors[name]?.message}</p>
    </>
};
export default () => {
    const { user } = useSelector(state => ({ user: state.userId }));
    const dispach = useDispatch();
    const onSubmit = (data) => {
        ///להוסיף מוצר כאן 
        // console.log(data);
        // console.log(product);
        // console.log(instructions);
        const recipe = { UserId: user, ...data };
        console.log(recipe);
        dispach({ type: actiontype.ADD_RECIPE, add: recipe })

    }


    const {
        register,
        handleSubmit,
        formState: { errors },
        control
    } = useForm({
        resolver: yupResolver(schema),
        // defaultValues:{  Name:r?.Name,
        //     CategoryId:r?.CategoryId,
        //     Img:r?.Img,
        //     Duration: r?.Duration,
        //     Difficulty: r?.Difficulty,
        //     Description:r?.Description,
        // },
    })
    const { fields, append, prepend, remove, swap, move, insert } = useFieldArray({
        control, // control props comes from useForm (optional: if you are using FormContext)
        name: "Ingrident", // unique name for your Field Array
    });
    const { fields:fieldsInstructions, append:appendInstructions,} = useFieldArray({
        control, // control props comes from useForm (optional: if you are using FormContext)
        name: "Instructions", // unique name for your Field Array
    });
    const [categories, SetCategories] = useState([" "]);
    // const [product, SetProduct] = useState([{ Name: "", Count: 0, Type: "" }]);
    const [category, SetCategory] = useState("");
    const [instructions, SetInstructions] = useState([""]);
    useEffect(() => {

        axios.get("http://localhost:8080/api/category")
            .then((data) => {
                SetCategories(data.data);
            });


    }, [category]);
    return <>
        <input type="text" name="category" onChange={(event) => {
            SetCategory(event.target.value)
        }} />
        <button onClick={() => {
            axios.get("http://localhost:8080/api/category", category)
                .then((data) => {
                    alert(category + `נוספה לרשימת קטגוריה`)
                }).catch(() => {
                    alert("ההוספה נכשלה")
                });
        }}>הוסף קטגוריה</button><br />

        <form onSubmit={handleSubmit(onSubmit)}>

            <br />
            {/* <Input Name="Name" errors={errors} register={register} label="שם עוגה:" place="enter name of cake"/> */}
            <label>שם עוגה:</label><br />
            <input placeholder="enter Name"{...register("Name")} />
            <p>{errors.Name?.message}</p>
            <label>קטגוריה:</label><br />
            <select id="categories" {...register("CategoryId")}>
                {categories.map((x) =>
                    <option value={x.Id}>{x.Name}</option>)}
            </select>
            <br />

            {/* <Input name="Img" errors={errors} register={register}label="קישור לתמונה:" /> */}

            <label>קישור לתמונה:</label><br />
            <input placeholder="enter your Img"{...register("Img")} />
            <p>{errors.Img?.message}</p>
            <label>רמת קושי:</label><br />
            <select id="Difficulty" {...register("Difficulty")}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            {/* <Input Name="Duration" errors={errors} register={register}label="משך זמן בדקות:" placeholder="enter your duration" /> */}

            <p>{errors.Difficulty?.message}</p>
            <label>משך זמן בדקות:</label><br />
            <input placeholder="enter your duration"{...register("Duration")} />
            <p>{errors.Duration?.message}</p>
            {/* <Input Name="Description" errors={errors} register={register}label="תאור קצר:" placeholder="enter your Difficulty"/> */}

            <label>תאור קצר:</label><br />
            <input placeholder="enter your Difficulty"{...register("Description")} />
            <p>{errors.Description?.message}</p>
            {/* {product.map((x, i) =>
                <div>
                    <label>שם מוצר:</label><br />
                    <input type="text" placeholder="הכנס שם מוצר" required onChange={(event) => product[i].Name = event.target.value} /><br />
                    <label>כמות:</label><br />
                    <input type="number" placeholder="הכנס כמות" required onChange={(event) => product[i].Count = event.target.value} /><br />
                    <label>סוג:</label><br />
