import { useForm } from "react-hook-form"
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup"
import { useLocation } from "react-router-dom"
import axios from "axios"
import { useDispatch } from "react-redux"
import * as actiontype from '../Store/actions'
import { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
const schema2 = yup.object({
    // Id: yup.number().required(),
    Name: yup.string().required(),
    Count: yup.number().required(),
    Type: yup.number().required(),
}).required()


const schema = yup
    .object({
        // Id: yup.number().required(),
        Name: yup.string().required(),
        UserId: yup.number().required(),
        CategoryId: yup.number().required(),
        Img: yup.string().required(),
        Duration: yup.number().required(),
        Difficulty: yup.number().required(),
        Description: yup.string().required(),
        Ingrident: yup.array({ Name:yup.string().required(), Count:yup.number().required(), Type:yup.string() }).required(),
        Instructions: yup.array().required(),
    }).required()
export default () => {
    const onSubmit = (data) => {
        ///להוסיף מוצר כאן 
    }
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(schema),
    })
    const [categories, SetCategories] = useState([]);
    const [product, SetProduct] = useState([{ Name: "", Count: 0, Type: "" }]);
    const [instruction, SetInstruction] = useState([" "]);
    useEffect(() => {

        axios.get("http://localhost:8080/api/category")
            .then((data) => {
                SetCategories(data.data)
            });
    }, []);
    return <>
        <form onSubmit={handleSubmit(onSubmit)}>
            <label>Name:</label><br />
            <input placeholder="enter Name"{...register("Name")} />
            <p>{errors.Name?.message}</p>

            <label>Category:</label><br />
            <select id="categories" {...register("CategoryId")}>
                {categories.map((x) =>
                    <option value={x.Id}>{x.Name}</option>)}
            </select>
            <button onClick={() => {
                return <> <label>Name Recipes:</label><br />
                    <input placeholder="enter Name" />
                    <p>{errors.Name?.message}</p>
                </>
            }}>הוסף קטגוריה</button><br/>

            <label>Img url:</label><br />
            <input placeholder="enter your Img"{...register("Img")} />
            <p>{errors.Img?.message}</p>

            <label>Difficulty:</label><br />
            <select id="Duration" {...register("Difficulty")}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            <p>{errors.Difficulty?.message}</p>

            {/* <input type="range" min="1" max="120" class="slider" style={{ color: "black" }} {...register("Difficulty")} />
            <p>{errors.Difficulty?.message}</p> */}
            <label>Duration:</label><br />
            <input placeholder="enter your Difficulty"{...register("Duration")} />
            <p>{errors.Duration?.message}</p>

            <label>Description:</label><br />
            <input placeholder="enter your Difficulty"{...register("Description")} />
            <p>{errors.Description?.message}</p>

            <label>Tz:</label><br />
            <button םמ>הופסת מוצר</button>
            <input placeholder="enter your Tz" {...register("Tz")} />
            <p>{errors.Tz?.message}</p>
            <label>Username:</label><br />
            <input placeholder="enter user name"{...register("Username")} />
            <p>{errors.Username?.message}</p>
            <label>Password:</label><br />
            <input placeholder="enter password"{...register("Password")}  />
            <p color="">{errors.Password?.message}</p>
            <button type="submit">הרשמה</button>
        </form>

    </>

}
