import axios from "axios";
import * as actiontype from './Store/actions'
import { useState,useEffect } from "react";
import './Recipes.css'
import { useDispatch, useSelector } from "react-redux";
export default () => {
  const {user ,Selected}= useSelector(state => ({user:state.userId,Selected:state.Selected}));
  const [recipes, SetRecipes] = useState([]);
  const [categories, SetCategories] = useState([]);
  const [category, SetCategory] = useState(null);
  const [duration, SetDuration] = useState(null);
  const [difficulty, SetDifficulty] = useState(null);
  const dispatch=useDispatch();
  useEffect(() => {
    axios.get("http://localhost:8080/api/recipe")
      .then((data) => {
        SetRecipes(data.data)
      });
    axios.get("http://localhost:8080/api/category")
      .then((data) => {
        SetCategories(data.data)
      });
      dispatch({ type: actiontype.SET_CATEDORY,category:category, duration:duration,difficulty:difficulty})

  }, [category, duration, difficulty]);
  return <>
    <div class="select">
      <label>קטגוריה:</label><br />
      <select id="categories"  onChange={event => {
        console.log(event.target.value);
        SetCategory(event.target.value);
      }}> {categories.map((x) =>
        <option value={x.Id}>{x.Name}</option>)}
      </select>
    </div>
    <div class="select">
      <label> :דרגת קושי</label><br />
      <select id="difficulty"  onChange={event => {
        console.log(event.target.value);
        SetDifficulty(event.target.value);
      }}>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
      </select>
    </div>
    <p>משך זמן הכנה:</p>
    <input type="range" min="1" max="100" class="slider" style={{color:"black"}} onChange={event => {
      console.log(event.target.value);
      SetDuration(event.target.value);
    }} />
    <br />
    <hi id="recipes"> -מתכונים- </hi>
    {recipes.map((x) => x.CategoryId==Selected.category&&x.Difficulty==Selected.difficulty&&x.Duration<=Selected.duration?
      <div class="recipy">
      <h2>{x.Name}</h2><br />
      <img src={x.Img} style={{ width: '15rem', height: '12rem' }} /><br />
      <button class="but">מוצרים</button>
      <button class="but">מתכון</button>
      {user == x.Id && <button class="but">עריכה</button>}
      {user == x.Id && <button class="but">מחיקה</button>}
    </div>:"")}</>


}
