import axios from "axios";
import { getRecipes, getCtegory } from './actions'
import * as actiontype from './actions'
const initialState = {
    userId: null,
    recipes: [],
    selected: null

}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actiontype.LOD_OUT: return { ...state, userId: null, selected: null, recipes: [] }
            break;
        case actiontype.LON_IN: return { ...state, userId: action.UserId }
            break;
        case actiontype.GET_RECIPES:
            const recipes = [...state.recipes];
            recipes.push(action.recipe);
            console.log(recipes);
            return { ...state, recipes: recipes };
            break;
        case actiontype.ADD_RECIPE: console.log(`d,cc`, action.add);
            axios.post(`http://localhost:8080/api/recipe`, action.add)
                .then((x) => {
                    alert(" נוסף ");
                }).catch(() => alert("שגיאה "));
            return { ...state, recipes: [...state.recipes, action.add] }
        case actiontype.DELETE_RECIPE:
            alert(action.Id);
            axios.post(`http://localhost:8080/api/recipe/delete/${action.Id}`)
                .then((x) => {
                    console.log(x.data);
                    alert("  נמחק");
                }).catch(() => alert("שגיאה "));
        case actiontype.EDIT_RECIPE: console.log(`אני בעדכון`, action.add);
            axios.post(`http://localhost:8080/api/recipe/edit`, action.add)
                .then((x) => {
                    alert("  עודכן  ");
                }).catch(() => alert(" שגיאה "));
            return { ...state}
        case actiontype.SET_CATEGORY: return { ...state, selected: action.selected }
        // case actiontype.SET_CATEDORY:return{...state,...state.Selected.category=action.category,...state.Selectedduration=action.duration,...state.Selected.difficulty=action.difficulty}


        default: return { ...state }
    }


}
export default reducer;
