import axios from "axios";
import * as actiontype from './actions'
const initialState={
    userId:null,
    recipes: [],
    Selected:{category:null,
        duration:null,
        difficulty:null
    }
    
}
console.log(category);

const reducer =(state=initialState,action)=>{
    switch(action.type)
    {
        case actiontype.LOD_OUT: return{...state ,userId:null}
        break;
        case actiontype.LON_IN:return{...state,userId:action.UserId}
        break;
        case actiontype.GET_RECIPES:return{...state,recipes:action.recipes}
        break;
        case actiontype.ADD_RECIPE :return {...state,recipes:[...state.recipes,action.add]}
        break;
        case actiontype.SET_CATEDORY:return{...state,category:action.category,duration:action.duration,difficulty:action.difficulty}
        break;
        default: return{...state}           
    }
    
}
export default reducer;
