import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import 'semantic-ui-css/semantic.min.css';
import React from 'react'
import { ButtonGroup } from "semantic-ui-react";
import { ButtonContent, Button, Icon } from 'semantic-ui-react'
const App = () => {
    const navigate = useNavigate();
    const user = useSelector(state => (state.userId));
    return <div>
        {/* {<ButtonGroup widths='6'>
            {user && <Button onClick={() => navigate('/HomePage')}>דף הבית </Button>}

            {user && <Button onClick={() => navigate('/Recipes')}>מתכונים</Button>}
            {user && <Button onClick={() => navigate('/Recipes', { state: user })}>מתכונים שלי</Button>}
            {user && <Button onClick={() => navigate('/ShopingList')}>רשימת קניות</Button>}
            {user && <Button onClick={() => navigate('/AddRecipes')}>הוספת מתכון</Button>}
            {user ? <Button onClick={() => { navigate('/SignUp'); alert("exit") }}>התנתקות</Button> : <div><Button onClick={() => navigate('/SignIn')}>הרשמה</Button><Button onClick={() => navigate('/LogIn')}>התחברות</Button></div>}
            {!user && <Button onClick={() => navigate('/About')}>אודות</Button>}
        </ButtonGroup>}.catch() */}
        {user && <button class="button" onClick={() => navigate('/HomePage')}>דף הבית </button>}
        {!user && <button class="button" onClick={() => navigate('/About')}>אודות</button>}
        {user && <button class="button" onClick={() => navigate('/Recipes')}>מתכונים</button>}
        {/* {<Button animated='vertical'>
            <ButtonContent hidden onClick={() => navigate('/ShopingList')}>Shop</ButtonContent>
            <ButtonContent visible >
                <Icon name='shop' />
            </ButtonContent>
        </Button>} */}
        {user && <button class="button" onClick={() => navigate('/Recipes', { state: user })}>מתכונים שלי</button>}
        {user && <button class="button" onClick={() => navigate('/AddRecipes')}>הוספת מתכון</button>}
        {user && <button class="button" onClick={() => navigate('/ShopingList')}>רשימת קניות</button>}
        {user ? <button class="button" onClick={() => { navigate('/SignUp'); alert("exit") }}>התנתקות</button> : <div><button class="button" onClick={() => navigate('/SignIn')}>הרשמה</button><button class="button" onClick={() => navigate('/LogIn')}>התחברות</button></div>}
    </div>
}
export default App;
