   }
   const addProduct = (product) => {
     
   const [operator, SetOperator] = useState(null);
   //   const  {shoppingList}  = useSelector(state => ({ shoppingList: state.shoppingList }));
   const dispach = useDispatch();
   useEffect(() => {
      getShoppingList();
      // dispach({type:actiontype.GET_SHOPPING_LIST});

      // if (operator != null)
      //    operator == -1 && product.Count - 1 == 0 ? axios.post(`http://localhost:8080/api/bay/edit`, product.Name, user, product.Count + operator).then((data) => {
      //       console.log(data)
      //    }) : axios.post(`http://localhost:8080/api/bay/delete/${user}/${product.Id}`).then((data) => console.log(data));
   }, []);
   const getShoppingList = () => {

      axios.get(`http://localhost:8080/api/bay/${user}`).
         then((x) => {

            console.log(x.data);

            SetShoppingList(x.data)

         }).catch(err => console.log(err))

   }
   const editProduct = (newProduct, oldProduct) => {
      alert(newProduct.Count);
      if (newProduct.Count+oldProduct.Count==0) {
         axios.post(`http://localhost:8080/api/bay/delete/${oldProduct.Id}`).then(() => {
            getShoppingList();
         }).catch(() => {})
      }
      else{
         axios.post(`http://localhost:8080/api/bay`, newProduct).then(()=>{
            getShoppingList();
         }).catch(()=>{console.log("error")})
      }
      
   }
      getShoppingList();
      return (<div>
         {shoppingList.map((product) =>
            <div class="product">
               <label>מוצר: </label>
               {product.Name}
               <label> כמות: </label>
               {product.Count}
               <button onClick={() => {
                  //  alert(`-1 ${product.Name}`)
                  newProduct = {...product};
                  newProduct.Count=-1;
                  editProduct(newProduct,product);   
                  if (newProduct.Count+product.Count==0&&shoppingList.length==1)
                  {
                     SetShoppingList([])
                  }
               }}>-</button>
               <button onClick={() => {
                  // alert(`+1 ${product.Name}`)
                  newProduct = {...product};
                  newProduct.Count=1;
                  editProduct(newProduct,product);
               }}>+</button>
            </div>
         )}
      </div>
      )
   
}
