import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from "react";
export default (userid) => {
   const [product, SetProduct] = useState()
   const [operator, SetOperator] = useState(null)
   let x;
   const user = useSelector(state => (state.userId));

   useEffect(() => {
      axios.get(`http://localhost:8080/api/bay/${user}`).then((data) => {
         x = data.data;
      }).catch(err => console.log(err));
      if(operator)
      operator==-1&&product.Count-1==0?axios.post(`http://localhost:8080/api/bay/edit`, product.Name, user, product.Count+operator).then((data) =>{
         console.log(data)}): axios.post(`http://localhost:8080/api/bay/delete/${user}/${product.Id}`).then((data) =>console.log(data));
   }, [operator]);
   return (<div>{x?.map((d) => {
      <div>
         <p>{d.Name}</p>
         <label>count:</label>
         <p>{d.Count}</p>
         <button onClick={() => {
            SetOperator(-1);
            SetProduct(d);
            alert(`-1 ${d.Name}`)
         }}>-</button>
         <button oonClick={() => {
            SetOperator(+1);
            alert(`+1 ${d.Name}`);
            SetProduct(d);
         }
         }>+</button></div>
   })}</div>
   )
}
