import { useForm } from "react-hook-form"
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup"
import { useLocation } from "react-router-dom"


const schema = yup
  .object({
    Name: yup.string().required(),
    Phone: yup.number().positive().required().max(10),
    Email: yup.string().email().required(),
    Tz: yup.string().required().min(9).max(9).required(),
    Username: yup.string().required(),
    Password: yup.string().required().min(3),
  })
  .required()


export default function App() {
  const {state}=useLocation()
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  })
  const onSubmit = (data) => console.log(data)


  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <label>Name</label>
      <input placeholder="enter Name"{...register("Name")} />
      <p>{errors.Name?.message}</p>

      <label>Phone</label>
      <input placeholder="enter your Phone" {...register("Phone")} />
      <p>{errors.Phone?.message}</p>

      <input placeholder="enter your Email"{...register("Email")} />
      <p>{errors.Email?.message}</p>

      <input placeholder="enter your Tz" {...register("Tz")} />
      <p>{errors.Tz?.message}</p>   
      <label>Username:</label>
      <input placeholder="enter user name"{...register("Username")} value={state?.Username} />
      <p>{errors.Username?.message}</p>
      <label>Password:</label>
      <input placeholder="enter password"{...register("Password")} value={state?.Password} />
      <p>{errors.Password?.message}</p>

      <button type="submit">הרשמה</button> 
    </form>
  )
}

