import { useForm } from "react-hook-form"
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup"
import { useLocation } from "react-router-dom"
import axios from "axios"
import { useDispatch } from "react-redux"
import * as actiontype from '../Store/actions'
import { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const schema = yup
    .object({
        // Id: yup.number().required(),
        Name: yup.string(),
        UserId: yup.number(),
        CategoryId: yup.number(),
        Img: yup.string(),
        // Duration: yup.number(),
        Difficulty: yup.number(),
        Description: yup.string(),
        // Ingrident: yup.array(),
        // Instructions: yup.array(),
    })
export default () => {
    const onSubmit = (data) => {
        ///להוסיף מוצר כאן 
        console.log(data);
        console.log(product);
    }
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(schema),
    })

    const [categories, SetCategories] = useState([]);
    const [product, SetProduct] = useState([ { Name: "", Count: 0, Type: "" }]);
    const [category, SetCategory] = useState("");
    const [instructions, SetInstructions] = useState([""]);
    useEffect(() => {

        axios.get("http://localhost:8080/api/category")
            .then((data) => {
                SetCategories(data.data);
            });
            

    }, [category]);
    return <>
        <input type="text" name="category" onChange={(event)=>{
            SetCategory(event.target.value)
        }}/>
        <button onClick={() => {
          axios.get("http://localhost:8080/api/category",category)
          .then((data) => {
            alert(category + `נוספה לרשימת קטגוריה`)
          }).catch(()=>{
            alert("ההוספה נכשלה")
          });
        }}>הוסף קטגוריה</button><br />
        <form onSubmit={handleSubmit(onSubmit)}>

            <br />
            <label>שם עוגה:</label><br />
            <input placeholder="enter Name"{...register("Name")} />
            <p>{errors.Name?.message}</p>
            <label>קטגוריה:</label><br />
            <select id="categories" {...register("CategoryId")}>
                {categories.map((x) =>
                    <option value={x.Id}>{x.Name}</option>)}
            </select>


            <label>קישור לתמונה:</label><br />
            <input placeholder="enter your Img"{...register("Img")} />
            <p>{errors.Img?.message}</p>

            <label>רמת קושי:</label><br />
            <select id="Difficulty" {...register("Difficulty")}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            <p>{errors.Difficulty?.message}</p>
            <label>משך זמן בדקות:</label><br />
            <input placeholder="enter your duration"{...register("Duration")} />
            <p>{errors.Duration?.message}</p>
            <label>תאור קצר:</label><br />
            <input placeholder="enter your Difficulty"{...register("Description")} />
            <p>{errors.Description?.message}</p>
            {product.map((x, i) => 
                <div>
                    <label>שם מוצר:</label><br/>
                    <input type="text" placeholder="Name" onChange={(event)=>product[i].Name=event.target.value}/><br/>
                    <label>כמות:</label><br/>
                    <input type="number" placeholder="Count" onChange={(event)=>product[i].Count=event.target.value} /><br/>
                    <label>סוג:</label><br/>
                    <input type="text" placeholder="Type" onChange={(event)=>product[i].Type=event.target.value}/><br/>
                </div>
            )} <button onClick={() => { SetProduct([...product, { Name: "", Count: 0, Type: "" }]) }}>הוספת מוצר</button>
            {instructions.map((x, i) => 
                <div>
                    <label>הוראה:</label><br/>
                    <input type="text" placeholder="Name" onChange={(event)=>instructions=event.target.value}/><br/>
                    <label>כמות:</label><br/>
                    <input type="number" placeholder="Count" onChange={(event)=>product[i].Count=event.target.value} /><br/>
                    <label>סוג:</label><br/>
                    <input type="text" placeholder="Type" onChange={(event)=>product[i].Type=event.target.value}/><br/>
                </div>
            )} <button onClick={() => { SetProduct([...product, { Name: "", Count: 0, Type: "" }]) }}>הוספת מוצר</button>
            <br />
            <button type="submit">הוספה</button>
        </form >

    </>

}
