import axios from "axios";
import * as actiontype from './actions'
const initialState={
    userId:null,
    Recipes: [],
    SelectedCategory:null
}
const reducer =(state=initialState,action)=>{
    switch(action.type)
    {
        case actiontype.LOD_OUT: return{...state ,userId:null}
        break;
        case actiontype.LON_IN:return{...state,userId:action.userId}
        break;
        case actiontype.GET_RECIPES :return{...state,recipes:action.recipes}
        break;
        case actiontype.ADD_RECIPE :return {...state,recipes:[...state.recipes,action.add]}
        break;
        case actiontype.SET_CATEDORY:return{...state,SelectedCategory:action.Category}
        break;
        default: return{...state}
           
    }
    
}
export default reducer;
