import { useForm } from "react-hook-form"
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup"


const schema = yup
  .object({
    name: yup.string().required(),
    phon: yup.number().positive().required().max(10),
    email: yup.string().email().required(),
    tz: yup.string().required().min(9).max(9).required(),
  })
  .required()


export default function App() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  })
  const onSubmit = (data) => console.log(data)


  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      
      <input placeholder="enter name"{...register("name")} />
      <p>{errors.name?.message}</p>

      <input placeholder="enter your Phon" {...register("phon")} />
      <p>{errors.phon?.message}</p>

      <input placeholder="enter your email"{...register("email")} />
      <p>{errors.email?.message}</p>

      <input placeholder="enter your tz" {...register("tz")} />
      <p>{errors.tz?.message}</p>   

      <input placeholder="enter user name"{...register("useName")} />
      <p>{errors.useName?.message}</p>
      
      <input placeholder="enter password"{...register("passWord")} />
      <p>{errors.passWord?.message}</p>

      <button type="submit">הרשמה</button> 
    </form>
  )
}

