import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { type } from "@testing-library/user-event/dist/type";
import * as actiontype from '../Store/actions'
export default () => {
   const user = useSelector(state => (state.userId));
   const [shoppingList, SetShoppingList] = useState([])


   const addProduct = (product) => {
      const findIndex = shoppingList.findIndex(x => x.Id == product.Id);
      axios.post(`http://localhost:8080/api/bay/edit`, { Name: product.Name, UserId: user, Count: product.Count + operator })
   }
   const [operator, SetOperator] = useState(null);
   //   const  {shoppingList}  = useSelector(state => ({ shoppingList: state.shoppingList }));
   const dispach = useDispatch();
   useEffect(() => {
      axios.get(`http://localhost:8080/api/bay/${user}`).then(data => {
         SetShoppingList(data.data);
      }).catch(() => { })
      // dispach({type:actiontype.GET_SHOPPING_LIST});

      // if (operator != null)
      //    operator == -1 && product.Count - 1 == 0 ? axios.post(`http://localhost:8080/api/bay/edit`, product.Name, user, product.Count + operator).then((data) => {
      //       console.log(data)
      //    }) : axios.post(`http://localhost:8080/api/bay/delete/${user}/${product.Id}`).then((data) => console.log(data));
   }, []);
   console.log(shoppingList);
   return (<div>
      {shoppingList.map((product) =>
         <div class="product">
            <label>מוצר: </label>
            {product.Name}
            <label> כמות: </label>
            {product.Count}
            <button onClick={() => {
               if (product.Count == 1) {
                  axios.post(`http://localhost:8080/api/bay/delete/${product.Id}`).then(() => {
                     alert("נמחק")
                  }).catch(() => {
                     alert("שגיאה ")
                  })
               }
            }}>-</button>
            <button onClick={() => {
               alert(`+1 ${product.Name}`)
            }}>+</button>
         </div>
      )}
   </div>
   )
}



